import React, { Component } from 'react';
import { GoogleMap, LoadScript } from '@react-google-maps';

class MapWithOverlay extends Component {
  constructor(props) {
    super(props);
    this.state = {
      map: null,
    };
  }

  componentDidMount() {
    const map = new google.maps.Map(document.getElementById('map'), {
      center: { lat: this.props.center.lat, lng: this.props.center.lng },
      zoom: this.props.zoom,
    });
    this.setState({ map });

    // Tạo overlay 
    const overlay = new google.maps.OverlayImage(
      this.props.overlayImage,
      {
        opacity: 0.7,
        position: { lat: this.props.center.lat, lng: this.props.center.lng },
        size: { width: this.props.overlayImageWidth, height: this.props.overlayImageHeight },
      }
    );
    map.overlayMapTypes.push(overlay);
  }

  render() {
    if (!this.state.map) return <div>Đang tải bản đồ...</div>;

    return (
      <LoadScript
        googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_KEY}
        libraries={['places']}
      >
        <GoogleMap
          id="map"
          map={this.state.map}
          options={{
            zoomControl: true,
            scrollwheel: true,
            streetViewControl: true,
          }}
        />
      </LoadScript>
    );
  }
}

export default MapWithOverlay;
