import anh from "../assets/test.jpg"
import React, { useState } from 'react';
import { GoogleMap, useLoadScript, OverlayView } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};

const center = {
  lat: 10.8231, // Vị trí mẫu
  lng: 106.6297, // Vị trí mẫu
};

const overlayImageStyle = (opacity) => ({
  width: '100%',
  height: '100%',
  opacity: opacity,
  position: 'absolute',
  top: 0,
  left: 0,
  pointerEvents: 'none' // Đảm bảo không can thiệp vào tương tác bản đồ
});

const GoogleMapOverlay = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'YOUR_GOOGLE_MAPS_API_KEY', // Thay bằng API key của bạn
  });

  const [opacity, setOpacity] = useState(0.5);

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps</div>;

  return (
    <div style={{ position: 'relative' }}>
      <GoogleMap mapContainerStyle={mapContainerStyle} center={center} zoom={10}>
        <OverlayView
          position={center}
          mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
        >
          <div style={{ width: '100vw', height: '100vh', position: 'relative' }}>
            <img
              src={anh} // Thay bằng URL ảnh bạn muốn phủ
              alt="Overlay"
              style={overlayImageStyle(opacity)}
            />
          </div>
        </OverlayView>
      </GoogleMap>
      <div style={{
        position: 'absolute',
        bottom: '10px',
        left: '50%',
        transform: 'translateX(-50%)',
        backgroundColor: 'white',
        padding: '10px',
        borderRadius: '5px',
        display: 'flex',
        alignItems: 'center',
      }}>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={opacity}
          onChange={(e) => setOpacity(parseFloat(e.target.value))}
          style={{ marginRight: '10px' }}
        />
        <span>{`Opacity: ${opacity.toFixed(2)}`}</span>
      </div>
    </div>
  );
};

export default GoogleMapOverlay;
