import anh from "../assets/test.jpg"
import "./GoogleMapOverlay.css"
import React, { useEffect, useState } from 'react';
import { GoogleMap, useLoadScript } from '@react-google-maps/api';
import {REACT_APP_GOOGLE_MAPS_KEY} from '../constants/constants'
const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};

const center = {
  lat: 21.1365, 
  lng: 105.8176, 
};

const overlayImageStyle = (scale, opacity) => ({
  width: `${scale * 100}%`,
  height: `${scale * 100}%`,
  opacity: opacity,
  position: 'absolute',
  top: 0,
  left: 0,
  zIndex: 1, 
  pointerEvents: 'none', // Đảm bảo không can thiệp vào tương tác bản đồ
});

const GoogleMapOverlay = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: REACT_APP_GOOGLE_MAPS_KEY, 
  });

  const [scale, setScale] = useState(1); // Initial scale
  const [zoom, setZoom] = useState(13); // Initial zoom level
  const [opacity, setOpacity] = useState(0.5); // Initial opacity
  const mapRef = useRef(null); // Reference to the Google Map instance
  useEffect(() => {
    // Update scale based on zoom level
    const newScale = Math.max(0.5, (zoom - 10) / 5); // Adjust scale as needed
    setScale(newScale);
  }, [zoom]);

  // Handle Ctrl + scroll zoom events
  useEffect(() => {
    const handleScroll = (event) => {
      if (event.ctrlKey && event.deltaY) {
        const zoomChange = event.deltaY > 0 ? -1 : 1; // Determine zoom direction
        const newZoom = zoom + zoomChange;
        if (newZoom >= 6 && newZoom <= 20) {
          setZoom(newZoom);
        }
      }
    };

    window.addEventListener('wheel', handleScroll);
    return () => window.removeEventListener('wheel', handleScroll);
  }, []);

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps</div>;

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
      <GoogleMap
         mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={zoom}
        options={{ disableDefaultUI: true }}
        onZoomChanged={(newZoom) => setZoom(newZoom)}
      />
      <img
        src={anh} 
        alt="Overlay"
        style={overlayImageStyle(scale, opacity)}
      />
      <div className="scroll" style={{
        position: 'absolute',
        bottom: '90%',
        left: '90%',
        transform: 'translateX(-50%)',
        backgroundColor: 'white',
        padding: '10px',
        borderRadius: '5px',
        zIndex: 2, // thanh kéo nằm trên cả overlay
        display: 'flex',
        alignItems: 'center',
      }}>
        <div className="scroll-item">
            <input
            className="input-scroll1"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={opacity}
            onChange={(e) => setOpacity(parseFloat(e.target.value))}
            style={{ marginRight: '10px', }}
            />
            <h5 className="input-scroll2">{`Opacity: ${opacity.toFixed(2)}`}</h5>

        </div>
      </div>
    </div>
  );
};

export default GoogleMapOverlay;
