import anh from "../assets/test.jpg"
import React, { useState } from 'react';
import { GoogleMap, useLoadScript } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100vw',
  height: 'auto',
};

const center = {
  lat: 10.8231, // Vị trí mẫu
  lng: 106.6297, // Vị trí mẫu
};

const overlayImageStyle = (opacity) => ({
  width: '100vw',
  height: '100vh',
  opacity: opacity,
  position: 'absolute',
  top: 0,
  left: 0,
  zIndex: 1, // Đảm bảo overlay nằm trên bản đồ
  pointerEvents: 'none', // Đảm bảo không can thiệp vào tương tác bản đồ
});

const GoogleMapOverlay = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyA3bsDl1xddiU_w38hA-fsGea8kWsp5uJM', // Thay bằng API key của bạn
  });

  const [opacity, setOpacity] = useState(0.5);

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps</div>;

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={10}
        options={{ disableDefaultUI: true }}
      />
      <img
        src={anh} // Thay bằng URL ảnh bạn muốn phủ
        alt="Overlay"
        style={overlayImageStyle(opacity)}
      />
      <div style={{
        position: 'absolute',
        bottom: '10px',
        left: '50%',
        transform: 'translateX(-50%)',
        backgroundColor: 'white',
        padding: '10px',
        borderRadius: '5px',
        zIndex: 2, // Đảm bảo thanh kéo nằm trên cả overlay
        display: 'flex',
        alignItems: 'center',
      }}>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={opacity}
          onChange={(e) => setOpacity(parseFloat(e.target.value))}
          style={{ marginRight: '10px' }}
        />
        <span>{`Opacity: ${opacity.toFixed(2)}`}</span>
      </div>
    </div>
  );
};

export default GoogleMapOverlay;
