    // content options
    children,
    style,
    className,
    pixelOffset,

    // open options
    anchor,
    shouldFocus,
    // events
    onClose,
    onCloseClick,

    ...infoWindowOptions
  } = props;

  // ## create infowindow instance once the mapsLibrary is available.
  const mapsLibrary = useMapsLibrary('maps');
  const [infoWindow, setInfoWindow] = useState<google.maps.InfoWindow | null>(
    null
