import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker, ImageOverlay } from 'react-google-maps';

const MapWithImageOverlay = () => {
  const [mapRef, setMapRef] = useState(null);

  const options = {
    center: { lat: 10.8536, lng: 106.6409 }, // Tọa độ mặc định
    zoom: 12, // Mức thu phóng mặc định
  };

  const handleMapRef = (ref) => {
    setMapRef(ref);
  };

  const handleOverlayClick = (event) => {
    console.log('Overlay clicked at:', event.latLng);
  };

  return (
    <LoadScript
      googleMapsApiKey="YOUR_API_KEY" // Thay thế bằng khóa API của bạn
      libraries={['places']}
    >
      <GoogleMap
        ref={handleMapRef}
        options={options}
        onClick={handleOverlayClick}
      >
        {/* Hiển thị ảnh bản đồ */}
        <ImageOverlay
          url="https://example.com/your-map-image.png" // Thay thế bằng URL ảnh bản đồ của bạn
          bounds={{ north: 10.9, south: 10.8, west: 106.6, east: 106.7 }}
          opacity={0.5}
        />

        {/* Thêm đánh dấu (marker) nếu cần */}
        <Marker position={{ lat: 10.8536, lng: 106.6409 }} />
      </GoogleMap>
    </LoadScript>
  );
};

export default MapWithImageOverlay;
