import React, { useState, useEffect, useRef } from 'react';

const MapComponent = () => {
  const [map, setMap] = useState(null);
  const [mapData, setMapData] = useState({
    center: { lat: 10.853, lng: 106.64 }, // Vị trí mặc định (Hà Nội)
    zoom: 13,
    markers: [],
    overlayImage: null, // Ảnh bản đồ
  });
  const mapRef = useRef(null);

  const handleOverlayImageChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      setMapData((prevState) => ({
        ...prevState,
        overlayImage: e.target.result,
      }));
    };
    reader.readAsDataURL(file);
  };

  useEffect(() => {
    const loadMap = () => {
      if (!window.google || !window.google.maps) {
        console.error('Google Maps API is not loaded.');
        return;
      }

      const googleMap = new google.maps.Map(mapRef.current, {
        center: mapData.center,
        zoom: mapData.zoom,
      });
      setMap(googleMap);
    };

    // Tải Google Maps API script
    if (!window.google || !window.google.maps) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${YOUR_API_KEY}&libraries=places&callback=${loadMap}`;
      script.async = true;
      document.body.appendChild(script);
    } else {
      loadMap();
    }
  }, []);

  useEffect(() => {
    if (map) {
      // Thêm markers
      mapData.markers.forEach((marker) => {
        new google.maps.Marker({
          position: marker.position,
          map: map,
        });
      });

      // Thêm overlay image
      if (mapData.overlayImage) {
        const overlay = new google.maps.ImageOverlay(mapData.overlayImage, {
          bounds: map.getBounds(),
          opacity: 0.5,
        });
        map.overlayLayer.addOverlay(overlay);
      }
    }
  }, [map, mapData.markers, mapData.overlayImage]);

  return (
    <div>
      <input type="file" onChange={handleOverlayImageChange} />
      <div ref={mapRef} style={{ width: '600px', height: '400px' }} />
    </div>
  );
};
